import React from "react";

import Employee from "./Employee";
import "./style.css";

export default function App() {
  const [role, setRole] = React.useState("Dev");
  const [employees, setEmplooyes] = React.useState([
    {
      name: "Max",
      role: "Devrel Engineer",
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800",
    },
    {
      name: "John",
      role: "Director of Eng.",
      img: "https://images.pexels.com/photos/775358/pexels-photo-775358.jpeg?auto=compress&cs=tinysrgb&w=800",
    },
    {
      name: "Corey",
      role: "The Devops guy",
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800",
    },
    {
      name: "Sal",
      role: "Manager",
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800",
    },
    {
      name: "Melanie",
      role: "Software Engineer",
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800",
    },
    {
      name: "Jake",
      role: "Senior Intern",
      img: "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800",
    },
  ]);

  return (
    <div className="App">
      <h1>Meet Our Employees</h1>
      <input
        type="text"
        onChange={(e) => {
          console.log(e.target.value);
          setRole(e.target.value);
        }}
      />
      <div className="flex flex-wrap justify-center">
        <Employee
          name={"Ash"}
          role={"Intern"}
          img="https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=500"
        />
        <Employee
          name={"Burak"}
          role={"Junior"}
          img="https://images.pexels.com/photos/6963102/pexels-photo-6963102.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Balika"}
          role={role}
          img="https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Sally"}
          role={"Senior"}
          img="https://images.pexels.com/photos/1382731/pexels-photo-1382731.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Ash"}
          role={"Intern"}
          img="https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Burak"}
          role={"Junior"}
          img="https://images.pexels.com/photos/6963102/pexels-photo-6963102.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Balika"}
          role={role}
          img="https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Sally"}
          role={"Senior"}
          img="https://images.pexels.com/photos/1382731/pexels-photo-1382731.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Ash"}
          role={"Intern"}
          img="https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
        <Employee
          name={"Burak"}
          role={"Junior"}
          img="https://images.pexels.com/photos/6963102/pexels-photo-6963102.jpeg?auto=compress&cs=tinysrgb&w=800"
        />
      </div>
    </div>
  );
}
