import React, { useState } from "react";
import Paper from "@mui/material/Paper";
import {
  Scheduler,
  AppointmentForm,
} from "@devexpress/dx-react-scheduler-material-ui";
import CustomForm from "./CustomForm";

const App = () => {
  const [appointments, setAppointments] = useState([
    {
      text: "Meeting",
      startDate: new Date(2022, 5, 15, 9, 30),
      endDate: new Date(2022, 5, 15, 11, 30),
    },
    {
      text: "Go to a gym",
      startDate: new Date(2022, 5, 15, 14, 0),
      endDate: new Date(2022, 5, 15, 15, 0),
    },
  ]);

  const handleAppointmentFormCreated = (data) => {
    data.form = (
      <CustomForm appointment={data.appointmentData} onClose={data.hideForm} />
    );
  };

  return (
    <Paper>
      <Scheduler
        dataSource={appointments}
        // onAppointmentFormCreated={handleAppointmentFormCreated}
      />
    </Paper>
  );
};

export default App;
