const TOP_CELL_OFFSET = 0.32;
const CELL_BOUND_OFFSET_PX = 1;

const getCellRect: GetCellRectHorizontalFn = (
  date, appointment, viewCellsData, viewMetaData, cellElementsMeta, takePrev, multiline,
) => {
  const cellIndex = multiline
    ? getMonthCellIndexByAppointmentData(
      viewCellsData, viewMetaData, date, appointment, takePrev,
    ) : getAllDayCellIndexByAppointmentData(
      viewCellsData, viewMetaData, date, appointment, takePrev,
    );

  const {
    top,
    left,
    width,
    height,
  } = cellElementsMeta.getCellRects[cellIndex]();
  const parentRect = cellElementsMeta.parentRect();
  return {
    top,
    left,
    width,
    height,
    parentRect,
  };
};

export const getHorizontalRectByAppointmentData: GetHorizontalRectByAppointmentDataFn = (
  appointment,
  viewMetaData,
  {
    multiline,
    viewCellsData,
    cellElementsMeta,
  },
) => {
  const firstCellRect = getCellRect(
    appointment.start.toDate(), appointment, viewCellsData,
    viewMetaData, cellElementsMeta, false, multiline,
  );
  const lastCellRect = getCellRect(
    appointment.end.toDate(), appointment, viewCellsData,
    viewMetaData, cellElementsMeta, true, multiline,
  );

  const top = firstCellRect.top + (firstCellRect.height * TOP_CELL_OFFSET);
  const height = firstCellRect.height - (firstCellRect.height * TOP_CELL_OFFSET);

  return {
    top: top - firstCellRect.parentRect.top,
    left: (firstCellRect.left - firstCellRect.parentRect.left) + CELL_BOUND_OFFSET_PX,
    width: ((lastCellRect.left - firstCellRect.left) + firstCellRect.width) - CELL_BOUND_OFFSET_PX,
    height,
    parentWidth: firstCellRect.parentRect.width,
  };
};
import moment from 'moment';
import {
  MonthCellsDataComputedFn, MonthCellData,
  CalculateMonthDateIntervalsFn, AppointmentMoment,
} from '../../types';
import { filterByViewBoundaries } from '../../utils';
import { sliceAppointmentByWeek } from './helpers';

const DAY_COUNT = 7;
const MONTH_LENGTH = 31;

export const monthCellsData: MonthCellsDataComputedFn = (
  currentDate,
  firstDayOfWeek,
  intervalCount = 1,
  today,
) => {
  const targetDate = moment(currentDate as Date);
  const currentMonths = [targetDate.month()];
  while (currentMonths.length < intervalCount) {
    currentMonths.push(targetDate.add(1, 'months').month());
  }
  const firstMonthDate = moment(currentDate as Date).date(1);
  const firstMonthDay = firstMonthDate.day() - firstDayOfWeek;
  const prevMonthDayCount = firstMonthDate.day(firstMonthDay).day() || DAY_COUNT;
  const prevMonth = moment(currentDate as Date).subtract(1, 'months');
  const prevMonthStartDay = prevMonth.daysInMonth() - (prevMonthDayCount - 1);
  const from = moment()
    .year(prevMonth.year())
    .month(prevMonth.month())
    .date(prevMonthStartDay)
    .startOf('day');

  const result: MonthCellData[][] = [];
  while (result.length < (Math.trunc((MONTH_LENGTH * intervalCount) / DAY_COUNT) + 2)) {
    const week: MonthCellData[] = [];
    while (week.length < DAY_COUNT) {
      week.push({
        startDate: from.toDate(),
        endDate: from.clone().add(1, 'day').toDate(),
        otherMonth: currentMonths.findIndex(month => month === from.month()) === -1,
        today: today ? moment(today as Date).isSame(from, 'date') : false,
      });
      from.add(1, 'day');
    }
    result.push(week);
  }
  return result;
};

export const calculateMonthDateIntervals: CalculateMonthDateIntervalsFn = (
  appointments, leftBound, rightBound,
) => [
  appointments
    .map(({ start, end, ...restArgs }) => ({ start: moment(start), end: moment(end), ...restArgs }))
    .reduce((acc, appointment) =>
      [...acc, ...filterByViewBoundaries(appointment, leftBound, rightBound, [], false)],
      [] as AppointmentMoment[],
    )
    .reduce((acc, appointment) => ([
      ...acc,
      ...sliceAppointmentByWeek(
        { left: moment(leftBound as Date), right: moment(rightBound as Date) },
        appointment,
        DAY_COUNT,
      ),
    ]), [] as AppointmentMoment[]),
];
