import moment from 'moment';
import { RRule, RRuleSet } from 'rrule';
import {
  AppointmentModel, PreCommitChangesFn, Changes, MakeDateSequenceFn, EditFn, DeleteFn, ChangeFn,
} from '../../types';
import { RECURRENCE_EDIT_SCOPE } from '../../constants';
import { getUTCDate, getRRuleSetWithExDates, formatDateToString } from '../../utils';

const mergeNewChanges = (
  appointmentData: Partial<AppointmentModel>, changes: Changes,
) => {
  const appointment = {
    ...appointmentData,
  };
  delete appointment.id;
  delete appointment.rRule;
  delete appointment.exDate;
  delete appointment.parentData;
  return { ...appointment, ...changes };
};

const reduceExDate = (prevExDate: string, boundDate: Date) => {
  if (prevExDate.length > 0) {
    return prevExDate.split(',').reduce((acc: string[], date: string) => {
      const momentDate = moment.utc(date);
      if (momentDate.isBefore(boundDate)) {
        return [...acc, date];
      }
      return acc;
    }, []).join(',');
  }
  return undefined;
};

const configureExDate = (exDate: string | undefined, date: Date) => {
  const currentExDate = `${moment.utc(date).format('YYYYMMDDTHHmmss')}Z`;
  return exDate
    ? `${exDate},${currentExDate}`
    : currentExDate;
};

const configureDateSequence: MakeDateSequenceFn = (rRule, exDate, prevStartDate, nextStartDate) => {
  const rruleSet = getRRuleSetWithExDates(exDate);

  const currentOptions = RRule.parseString(rRule as string);
  const correctedOptions = currentOptions.until
    ? { ...currentOptions, until: moment(getUTCDate(currentOptions.until)).toDate() }
    : currentOptions;
  const prevStartDateUTC = moment(getUTCDate(prevStartDate!)).toDate();
  rruleSet.rrule(new RRule({
    ...correctedOptions,
    dtstart: prevStartDateUTC,
  }));
  if (currentOptions.count || currentOptions.until) {
    return {
      options: currentOptions,
      dates: rruleSet.all()
      // we shouldn't use `new Date(string)` because this function has different results in Safari
      .map(nextDate => moment(formatDateToString(nextDate)).toDate()),
    };
  }
  const leftBound = prevStartDateUTC;
  const rightBound = moment(getUTCDate(nextStartDate!)).toDate();
  return {
    options: currentOptions,
    dates: rruleSet.between(leftBound, rightBound, true)
    .map(nextDate => moment(formatDateToString(nextDate)).toDate()),
  };
};

const configureICalendarRules = (rRule: string | undefined, options: object) => {
  const rruleSet = new RRuleSet();
  rruleSet.rrule(new RRule({
    ...RRule.parseString(rRule as string),
    ...options,
  }));
  return rruleSet.valueOf();
};

const changeCurrentAndFollowing: ChangeFn = (appointmentData, changes, changeAllAction) => {
  const { rRule, startDate, parentData, exDate: prevExDate = '', id } = appointmentData;

  const { initialSequence, currentChildIndex } = getAppointmentSequenceData(
    parentData.startDate, moment.utc(startDate as Date).toDate(), prevExDate, rRule,
  );

  if (currentChildIndex === 0) return changeAllAction(appointmentData, changes);

  const changedRules = configureICalendarRules(rRule as string, {
    dtstart: moment.utc(parentData.startDate).toDate(),
    until: moment.utc(initialSequence[currentChildIndex - 1]).toDate(),
    count: null,
  });

  const nextExDate = reduceExDate(prevExDate, startDate as Date);
  return {
    changed: {
      [id!]: {
        rRule: changedRules[1].slice(6),
        ...nextExDate && prevExDate !== nextExDate ? { exDate: nextExDate } : {},
      },
    },
  };
};

const getAppointmentSequenceData = (
  prevStartDate: Date, startDate: Date, exDate: string, rRule: string | undefined,
) => {
  const initialSequence = configureDateSequence(rRule, exDate,
    moment.utc(prevStartDate).toDate(), moment.utc(startDate).toDate(),
  ).dates;
  const currentChildIndex = initialSequence
    .findIndex(date => moment(date).isSame(startDate as Date));
  return { initialSequence, currentChildIndex };
};

export const deleteCurrent: DeleteFn = (appointmentData) => {
  const { options, dates } = configureDateSequence(
    appointmentData.rRule, appointmentData.exDate,
    moment.utc(appointmentData.parentData.startDate).toDate(),
    moment.utc(appointmentData.startDate).toDate(),
  );

  if ((options.count || options.until) && dates.length === 1) {
    return deleteAll(appointmentData);
  }

  const nextExDate = configureExDate(appointmentData.exDate, appointmentData.startDate as Date);
  return { changed: { [appointmentData.id!]: { exDate: nextExDate } } };
};

export const deleteAll: DeleteFn = (appointmentData) => {
  return { deleted: appointmentData.id };
};

export const deleteCurrentAndFollowing: DeleteFn = appointmentData => changeCurrentAndFollowing(
  appointmentData, {}, deleteAll,
);

export const editAll: EditFn = (appointmentData, changes) => {
  const { rRule, id } = appointmentData;

  const initialRule = new RRule(RRule.parseString(rRule as string));
  if (changes.startDate
    && moment.utc(changes.startDate as Date).isAfter(initialRule.options.until!)) {
    return {
      changed: {
        [id!]: {
          ...changes,
          rRule: 'FREQ=DAILY;COUNT=1',
          exDate: '',
        },
      },
    };
  }

  return  { changed: {  [appointmentData.id!]: changes } };
};

export const editCurrent: EditFn = (appointmentData, changes) => ({
  changed: {
    [appointmentData.id!]: {
      exDate: configureExDate(appointmentData.exDate, appointmentData.startDate as Date),
    },
  },
  added: mergeNewChanges(appointmentData as Partial<AppointmentModel>, changes as Changes),
});

export const editCurrentAndFollowing: EditFn = (appointmentData, changes) => {
  const { rRule, startDate, exDate: prevExDate = '', parentData } = appointmentData;
  const initialRule = new RRule(RRule.parseString(rRule as string));

  const { initialSequence, currentChildIndex } = getAppointmentSequenceData(
    parentData.startDate, moment.utc(startDate as Date).toDate(),  prevExDate, rRule,
  );
  if (currentChildIndex === 0) return editAll(appointmentData, changes);

  const addedOptions = initialRule.options.count || initialRule.options.until
    ? { count: initialSequence.length - currentChildIndex }
    : {};
  const addedRules = configureICalendarRules(appointmentData.rRule as string, {
    dtstart: moment.utc(startDate as Date).toDate(),
    ...addedOptions,
  });

  const addedAppointment = moment.utc(changes.startDate as Date).isAfter(initialRule.options.until!)
    ? { rRule: 'FREQ=DAILY;COUNT=1', exDate: '' } : { rRule: addedRules[1].slice(6) };

  return {
    changed: changeCurrentAndFollowing(appointmentData, changes, editAll).changed,
    added: {
      ...addedAppointment, ...mergeNewChanges(appointmentData, changes),
    },
  };
};

export const preCommitChanges: PreCommitChangesFn = (
  changes, appointmentData, editType,
) => {
  if (changes === null) {
    switch (editType) {
      case RECURRENCE_EDIT_SCOPE.ALL: {
        return deleteAll(appointmentData);
      }
      case RECURRENCE_EDIT_SCOPE.CURRENT: {
        return deleteCurrent(appointmentData);
      }
      case RECURRENCE_EDIT_SCOPE.CURRENT_AND_FOLLOWING: {
        return deleteCurrentAndFollowing(appointmentData);
      }
    }
  } else {
    switch (editType) {
      case RECURRENCE_EDIT_SCOPE.ALL: {
        return editAll(appointmentData, changes);
      }
      case RECURRENCE_EDIT_SCOPE.CURRENT: {
        return editCurrent(appointmentData, changes);
      }
      case RECURRENCE_EDIT_SCOPE.CURRENT_AND_FOLLOWING: {
        return editCurrentAndFollowing(appointmentData, changes);
      }
    }
  }
  return {};
};
import moment from 'moment';
import { PureComputed } from '@devexpress/dx-core';
import {
  ViewCell, ClientOffset, TimeType, ScrollingStrategy,
  AllDayCell, CalculateAppointmentTimeBoundaries,
  TimeBoundariesByDrag, TimeBoundariesByResize, AppointmentModel,
  CellElementsMeta, Grouping, ValidResource, Group, SchedulerDateTime, GroupOrientation,
} from '../../types';
import {
  VERTICAL_TYPE, HORIZONTAL_TYPE, SCROLL_OFFSET, MINUTES,
  SECONDS, RESIZE_TOP, RESIZE_BOTTOM, HOURS,
} from '../../constants';
import {
  allDayRects, horizontalTimeTableRects, verticalTimeTableRects,
} from '../common/calculate-rects';

const clamp: PureComputed<
  [number, number, number]
> = (value, min, max) => Math.max(Math.min(value, max), min);

const calculateInsideOffset: PureComputed<
  [string, number, number], number
> = (targetType, insidePart, cellDurationMinutes) => targetType === VERTICAL_TYPE
  ? insidePart * cellDurationMinutes * 60 / 2 : 0;

export const cellType: PureComputed<
  [ViewCell | AllDayCell], string
> = data => moment(data.startDate as Date)
  .isSame(data.endDate as Date, 'day') ? VERTICAL_TYPE : HORIZONTAL_TYPE;

export const intervalDuration: PureComputed<
  [ViewCell | AllDayCell, TimeType], number
> = (data, type) => moment(data.endDate as Date).diff(data.startDate as Date, type);

export const cellIndex: PureComputed<
  [Array<() => ClientRect>, ClientOffset], number
> = (getCellRects, clientOffset) => getCellRects.findIndex((getCellRect) => {
  const { left, top, right, bottom } = getCellRect();
  const isOver = clientOffset
      && clamp(clientOffset.x, left, right) === clientOffset.x
      && clamp(clientOffset.y, top, bottom) === clientOffset.y;
  return isOver;
});

export const cellData: PureComputed<
  [number, number, ViewCell[][], ViewCell[][]], ViewCell | AllDayCell
> = (timeTableIndex, allDayIndex, viewCellsData, allDayCellsData) => {
  const cellsData = allDayIndex !== -1 ? allDayCellsData : viewCellsData;
  const currentIndex = allDayIndex !== -1 ? allDayIndex : timeTableIndex;
  const tableWidth = cellsData[0].length;

  const rowIndex = Math.floor(currentIndex / tableWidth);
  const columnIndex = currentIndex % tableWidth;
  return cellsData[rowIndex][columnIndex];
};

export const autoScroll: PureComputed<
  [ClientOffset, ScrollingStrategy, number], void
> = (clientOffset, scrollingStrategy, scrollSpeed) => {
  scroll(
    clientOffset.y, scrollingStrategy.topBoundary, scrollingStrategy.bottomBoundary,
    scrollingStrategy.changeVerticalScroll, scrollSpeed,
  );
  scroll(
    clientOffset.x, scrollingStrategy.leftBoundary, scrollingStrategy.rightBoundary,
    scrollingStrategy.changeHorizontalScroll, scrollSpeed,
  );
};

const scroll: PureComputed<
  [number, number, number, (value: number) => void, number], void
> = (offset, firstBoundary, secondBoundary, changeScroll, scrollSpeed) => {
  if ((offset < firstBoundary + SCROLL_OFFSET) && (offset > firstBoundary)) {
    changeScroll(-scrollSpeed);
  }
  if (secondBoundary - SCROLL_OFFSET < offset) {
    changeScroll(+scrollSpeed);
  }
};

export const timeBoundariesByResize: TimeBoundariesByResize = (
  payload, targetData, targetType, cellDurationMinutes, insidePart,
) => {
  if (targetType !== payload.appointmentType) {
    return { appointmentStartTime: undefined, appointmentEndTime: undefined };
  }

  let appointmentStartTime;
  let appointmentEndTime;
  const sourceType = payload.type;

  if (sourceType === RESIZE_TOP) {
    const insideTopOffset = calculateInsideOffset(targetType, insidePart, cellDurationMinutes);
    appointmentStartTime = moment(targetData.startDate as Date)
      .add(insideTopOffset, SECONDS).toDate();
    appointmentEndTime = moment(payload.endDate as Date).toDate();
  }
  if (sourceType === RESIZE_BOTTOM) {
    const insideBottomOffset = insidePart === 0 && targetType === VERTICAL_TYPE
      ? cellDurationMinutes * 60 / 2 : 0;
    appointmentEndTime = moment(targetData.endDate as Date)
      .add(-insideBottomOffset, SECONDS).toDate();
    appointmentStartTime = moment(payload.startDate as Date).toDate();
  }
  // keep origin appointment duration if coordinates are wrong
  if (moment(appointmentEndTime).diff(appointmentStartTime, MINUTES) < 1) {
    appointmentStartTime = moment(payload.startDate as Date).toDate();
    appointmentEndTime = moment(payload.endDate as Date).toDate();
  }
  return { appointmentStartTime, appointmentEndTime };
};

export const timeBoundariesByDrag: TimeBoundariesByDrag = (
  payload, targetData, targetType,
  cellDurationMinutes, insidePart, offsetTimeTopBase,
) => {
  if (targetType === HORIZONTAL_TYPE
    && intervalDuration(payload, SECONDS) < intervalDuration(targetData, SECONDS)) {
    return {
      appointmentStartTime: targetData.startDate as Date,
      appointmentEndTime: targetData.endDate as Date,
      offsetTimeTop: 0,
    };
  }
  let offsetTimeTop;
  let appointmentStartTime;
  let appointmentEndTime;

  const insideOffset = calculateInsideOffset(targetType, insidePart, cellDurationMinutes);
  const start = moment(targetData.startDate as Date).add(insideOffset, SECONDS);

  if (offsetTimeTopBase === null) {
    offsetTimeTop = moment(targetData.startDate as Date)
      .diff(payload.startDate as Date, SECONDS) + insideOffset;
  } else {
    offsetTimeTop = offsetTimeTopBase;
  }

  if (payload.type === targetType) {
    const appointmentDurationSeconds = intervalDuration(payload, SECONDS);
    appointmentStartTime = moment(start).add((offsetTimeTop) * (-1), SECONDS).toDate();
    appointmentEndTime = moment(start)
      .add((appointmentDurationSeconds - offsetTimeTop), SECONDS).toDate();
  } else {
    appointmentStartTime = moment(targetData.startDate as Date)
      .add(insideOffset, SECONDS).toDate();
    appointmentEndTime = moment(targetData.endDate as Date).add(insideOffset, SECONDS).toDate();
  }

  return { appointmentStartTime, appointmentEndTime, offsetTimeTop };
};

export const calculateAppointmentTimeBoundaries: CalculateAppointmentTimeBoundaries = (
  payload, targetData, targetType,
  cellDurationMinutes, insidePart, offsetTimeTopBase,
) => {
  const isDragging = (payload.type === VERTICAL_TYPE || payload.type === HORIZONTAL_TYPE);

  return(isDragging
    ? timeBoundariesByDrag(
        payload, targetData as AppointmentModel, targetType,
        cellDurationMinutes, insidePart, offsetTimeTopBase,
      )
    : timeBoundariesByResize(
        payload, targetData as AppointmentModel, targetType, cellDurationMinutes, insidePart,
      )
  );
};

export const calculateInsidePart: PureComputed<
  [number, Array<() => ClientRect>, number]
> = (top, timeTableCellsRects, timeTableIndex) => {
  if (timeTableIndex !== undefined && timeTableIndex !== -1) {
    const cellRect = timeTableCellsRects[timeTableIndex]();
    return top > cellRect.top + cellRect.height / 2 ? 1 : 0;
  }
  return 0;
};

export const calculateDraftAppointments = (
  allDayIndex: number, draftAppointments: any, startViewDate: Date,
  endViewDate: Date, excludedDays: number[], viewCellsData: any,
  getAllDayCellsElementRects: CellElementsMeta,
  targetType: string, cellDurationMinutes: number,
  getTableCellElementRects: CellElementsMeta,
  grouping: Grouping[], resources: ValidResource[], groups: Group[][],
  groupOrientation: GroupOrientation, groupedByDate: boolean,
) => {
  if (allDayIndex !== -1 || (targetType === VERTICAL_TYPE
    && getAllDayCellsElementRects.getCellRects.length
    && intervalDuration(draftAppointments[0].dataItem, HOURS) > 23)) {
    const allDayDrafts = draftAppointments.map((draftAppt: any) => ({
      ...draftAppt,
      allDay: true,
    }));

    return {
      allDayDraftAppointments: allDayRects(
        allDayDrafts, startViewDate, endViewDate,
        excludedDays, viewCellsData, getAllDayCellsElementRects,
        grouping, resources, groups, groupOrientation, groupedByDate,
      ),
      timeTableDraftAppointments: [],
    };
  }

  if (targetType === VERTICAL_TYPE || allDayIndex !== -1) {
    return {
      allDayDraftAppointments: [],
      timeTableDraftAppointments: verticalTimeTableRects(
        draftAppointments, startViewDate, endViewDate,
        excludedDays, viewCellsData, cellDurationMinutes, getTableCellElementRects,
        grouping, resources, groups, groupOrientation, groupedByDate,
      ),
    };
  }
  return {
    allDayDraftAppointments: [],
    timeTableDraftAppointments: horizontalTimeTableRects(
      draftAppointments, startViewDate, endViewDate,
      viewCellsData, getTableCellElementRects,
      grouping, resources, groups, groupOrientation, groupedByDate,
    ),
  };
};

export const calculateAppointmentGroups: PureComputed<
  [Array<Group> | undefined, Array<ValidResource>, AppointmentModel], any
> = (cellGroupingInfo, resources, appointmentData) => {
  if (!cellGroupingInfo) return {};
  return cellGroupingInfo.reduce((acc, group: Group) => {
    const isMultipleResource = resources.find(
      resource => (resource.fieldName === group.fieldName),
    )!.allowMultiple;
    return {
      ...acc,
      [group.fieldName]: isMultipleResource
        ? updateMultipleResourceInfo(group, appointmentData) : group.id,
    };
  }, {});
};

const updateMultipleResourceInfo: PureComputed<
  [Group, AppointmentModel], any
> = (cellResource, appointmentData) => {
  const appointmentGroupItems = appointmentData[cellResource.fieldName];
  if (appointmentGroupItems.findIndex((groupItem: any) => groupItem === cellResource.id) !== -1) {
    return appointmentGroupItems;
  }
  return [cellResource.id];
};

export const appointmentDragged: PureComputed<
  [SchedulerDateTime, SchedulerDateTime, SchedulerDateTime, SchedulerDateTime, any, any], boolean
> = (start, startPrev, end, endPrev, groupingInfo, groupingInfoPrev) => {
  if (moment(start as Date).isSame(startPrev as Date)
      && moment(end as Date).isSame(endPrev as Date)
      && groupingInfoNotChanged(groupingInfo, groupingInfoPrev)) {
    return false;
  }
  return true;
};

const groupingInfoNotChanged: PureComputed<
  [any, any], boolean
> = (groupingInfo, groupingInfoPrev) => {
  const fields = Object.getOwnPropertyNames(groupingInfo);
  return fields.every((field) => {
    if (Array.isArray(groupingInfo[field]) && Array.isArray(groupingInfoPrev[field])) {
      return groupingInfo[field].every((item: any, index: number) => (
        item === groupingInfoPrev[field][index]
      ));
    }
    return groupingInfo[field] === groupingInfoPrev[field];
  });
};
import { PureComputed } from '@devexpress/dx-core';
import moment from 'moment';
import {
  IsCellShadedFn, IsShadedAppointment,
  GetCurrentTimeIndicatorTopFn,
} from '../../types';

export const isMonthCell: PureComputed<
  [boolean | undefined], boolean
> = otherMonth => otherMonth !== undefined;

export const isShadedAppointment: IsShadedAppointment = (
  { data: appointmentData }, currentTime, shadePreviousAppointments,
) => {
  const momentCurrentDate = moment(currentTime);
  if (appointmentData.allDay) {
    return momentCurrentDate.isAfter(appointmentData.endDate as Date, 'day')
    && shadePreviousAppointments;
  }
  if (momentCurrentDate.isAfter(appointmentData.endDate as Date)) {
    return shadePreviousAppointments;
  }
  return false;
};

export const getCurrentTimeIndicatorTop: GetCurrentTimeIndicatorTopFn = (
  cellData, currentTime,
) => {
  const top = ((currentTime - cellData.startDate.getTime()) * 100)
  / (cellData.endDate.getTime() - cellData.startDate.getTime());
  return (top < 0 || top > 100) ? undefined : `${top}%`;
};

export const isCellShaded: IsCellShadedFn = (
  { startDate, endDate, otherMonth }, currentTime, shadePreviousCells,
) => {
  const monthCell = isMonthCell(otherMonth);
  return ((startDate.getTime() < currentTime && !monthCell)
    || endDate.getTime() < currentTime && monthCell) && shadePreviousCells;
};
import { GetAppointmentResources, ValidResourceInstance, ValidResource } from '../../types';
import { PureComputed } from '@devexpress/dx-core';

export const getAppointmentResources: GetAppointmentResources = (
  appointment, resources, plainResources,
) => {
  if (
    !resources || resources.length === 0
    || !plainResources || plainResources.length === 0
  ) return [];

  return resources.reduce((acc, resource) => {
    const appointmentResourceId = appointment[resource.fieldName];
    if (appointmentResourceId === undefined) return acc;

    if (resource.allowMultiple && !Array.isArray(appointmentResourceId)
    || !resource.allowMultiple && Array.isArray(appointmentResourceId)) {
      // throw error
      return acc;
    }

    if (resource.allowMultiple) {
      return [
        ...acc,
        ...(appointmentResourceId as Array<number | string>)
        .reduce((prevResources, itemId) => addResourceToAppointmentResources(
          plainResources, prevResources, resource, itemId,
        ) as Array<ValidResourceInstance>, [] as Array<ValidResourceInstance>),
      ];
    }

    return addResourceToAppointmentResources(
      plainResources, acc, resource, appointmentResourceId,
    ) as Array<ValidResourceInstance>;
  }, [] as Array<ValidResourceInstance>);
};

const addResourceToAppointmentResources: PureComputed<
  [Array<ValidResourceInstance>, Array<ValidResourceInstance>, ValidResource,
  number | string], Array<ValidResourceInstance>
> = (plainResources, appointmentResources, resource, resourceId) => {
  const currentResource = plainResources.find(
    plainItem => resource.fieldName === plainItem.fieldName && plainItem.id === resourceId,
  );

  return currentResource ? [
    ...appointmentResources,
    currentResource!,
  ] : appointmentResources;
};
import {
  ConvertResourcesToPlain, ValidateResources, ValidResourceInstance, AddResourcesToAppointments,
} from '../../types';
import { getAppointmentResources } from './helpers';

export const convertResourcesToPlain: ConvertResourcesToPlain = (validResources) => {
  return validResources.reduce((acc, resource) => [
    ...acc,
    ...resource.instances.map(item => item),
  ], [] as Array<ValidResourceInstance>);
};

export const validateResources: ValidateResources = (resources, mainResourceName, palette) => {
  const isMainResourceDefined = !!mainResourceName;
  let currentPaletteIndex = 0;
  return resources.map((resource, groupIndex) => {
    const fieldName = resource.fieldName;
    const isMain = isMainResourceDefined && mainResourceName === fieldName
      || groupIndex === 0 && !isMainResourceDefined;
    const title = resource.title || fieldName;
    const allowMultiple = !!resource.allowMultiple;
    return {
      fieldName,
      isMain,
      title,
      allowMultiple,
      instances: resource.instances.map((resourceItem) => {
        const color = resourceItem.color || palette[currentPaletteIndex % palette.length];
        if (!resourceItem.color) currentPaletteIndex += 1;

        return ({
          id: resourceItem.id,
          color,
          fieldName,
          text: resourceItem.text || title || fieldName,
          title,
          allowMultiple,
          isMain,
        });
      }),
    };
  });
};

export const addResourcesToAppointments: AddResourcesToAppointments = (
  appointments, resources, plainResources,
) => [
  appointments.map(appointment => ({
    ...appointment,
    resources: getAppointmentResources(appointment.dataItem, resources, plainResources),
  })),
];
import { PureReducer, slice } from '@devexpress/dx-core';
import {
  ResourceGroupingState, ToggleGroupPayload,
} from '../../types';

export const toggleExpandedGroups: PureReducer<ResourceGroupingState, ToggleGroupPayload> = (
  state, { groupKey },
) => {
  const expandedGroups = slice(state.expandedGroups);
  const groupKeyIndex = expandedGroups.indexOf(groupKey);

  if (groupKeyIndex > -1) {
    expandedGroups.splice(groupKeyIndex, 1);
  } else {
    expandedGroups.push(groupKey);
  }

  return { expandedGroups };
};
import moment, { Moment } from 'moment';
import { ViewCell } from '../../types';
import { PureComputed } from '@devexpress/dx-core';

export const getDayScaleCellColSpan: PureComputed<
  [ViewCell[][]], number
> = (cellsData) => {
  const firstDate = cellsData[0][0].startDate;
  let count = 1;
  while (cellsData[0][count] && moment(cellsData[0][count].startDate).isSame(firstDate, 'day')) {
    count += 1;
  }
  return count;
};

export const getDayScaleCells: PureComputed<
[ViewCell[][], boolean], ViewCell[]
> = (cellsData, groupedByDate) => {
  if (!groupedByDate) {
    return cellsData[0].map(({
      startDate, endDate, today, endOfGroup, groupingInfo,
    }, index) => ({
      key: index.toString(),
      startDate, endDate, today, endOfGroup, groupingInfo,
    }));
  }
  let prevDate: Moment;
  const colSpan = getDayScaleCellColSpan(cellsData);
  return cellsData[0].reduce((acc, { startDate, endDate, today }, index) => {
    const currentDate = moment(startDate);
    if (currentDate.isSame(prevDate)) {
      return acc;
    }
    prevDate = currentDate;
    return [
      ...acc, {
        key: index.toString(),
        startDate, endDate, today, colSpan,
        endOfGroup: true,
      },
    ];
  }, [] as ViewCell[]);
};
