import React, { useState } from "react";
import {
  Scheduler,
  AppointmentForm,
} from "@devexpress/dx-react-scheduler-material-ui";

const CustomForm = ({ appointment, onClose }) => {
  const [startDate, setStartDate] = useState(appointment.startDate);
  const [endDate, setEndDate] = useState(appointment.endDate);
  const [subject, setSubject] = useState(appointment.subject);
  const [location, setLocation] = useState(appointment.location);

  const handleSave = () => {
    // Perform the necessary actions to save the changes to the appointment
    // for example, you can use the Scheduler's `updateAppointment` method
    Scheduler.updateAppointment(appointment.id, {
      subject: subject,
      location: location,
      startDate: startDate,
      endDate: endDate,
    });
    onClose();
  };

  return (
    <AppointmentForm
      appointmentData={appointment}
      startDateExpr={setStartDate}
      endDateExpr={setEndDate}
      subjectExpr={setSubject}
      locationExpr={setLocation}
      // onSaveButtonClick={handleSave}
      onCancelButtonClick={onClose}
    />
  );
};

export default CustomForm;
