const M366RANGE = [0, 31, 60, 91, 121, 152, 182, 213, 244, 274, 305, 335, 366]
const M365RANGE = [0, 31, 59, 90, 120, 151, 181, 212, 243, 273, 304, 334, 365]

const WDAYMASK = (function () {
  let wdaymask: number[] = []
  for (let i = 0; i < 55; i++) wdaymask = wdaymask.concat(range(7))
  return wdaymask
