import React from "react";

import Employee from "./Employee";
import "./style.css";

export default function App() {
  const [role, setRole] = React.useState("Dev");
  const isValid = true;

  return (
    <div className="App bg-red-300">
      <h1>Hello CodeSandbox</h1>
      <input
        type="text"
        onChange={(e) => {
          console.log(e.target.value);
          setRole(e.target.value);
        }}
      />
      {isValid ? (
        <>
          <Employee name={"Ash"} role={"Intern"} />
          <Employee name={"Burak"} role={"Junior"} />
          <Employee name={"Murik"} role={role} />
          <Employee name={"Sally"} role={"Senior"} />
        </>
      ) : (
        <p>Employee's name is not valid</p>
      )}
    </div>
  );
}
