import React from "react";

import User from "./User/User";

const DUMMY_DATA = [
  { name: "Max", age: 35 },
  { name: "Sam", age: 33 },
  { name: "Ash", age: 25 },
  { name: "Joshua", age: 28 },
];

function App() {
  const [state, setUserState] = React.useState("");

  const clickHandler = () => {};
  setUserState(DUMMY_DATA);
  return (
    <div style={{ width: "100%", textAlign: "center", marginTop: "5rem" }}>
      {console.log(state)}
      <h1>Our Employee</h1>
      <button onClick={clickHandler}>Switch</button>
      <User name={state[0].name} age={state[0].age} />
      <User name={state[1].name} age={state[1].age}>
        My Favorite Things: Swiming, Racing
      </User>
      <User name={state[2].name} age={state[2].age} />
      <User name={state[3].name} age={state[3].age} />
    </div>
  );
}

export default App;
