import React from "react";

import User from "./User/User";

function App() {
  const [userState, setUserState] = React.useState({
    users: [
      { name: "Max", age: 35 },
      { name: "Sam", age: 33 },
      { name: "Ash", age: 25 },
      { name: "Joshua", age: 28 },
    ],
    otherState: "some other value",
  });

  const clickHandler = () => {
    setUserState({
      users: [
        { name: "Maximilian", age: 28 },
        { name: "Manu", age: 29 },
        { name: "Stephanie", age: 27 },
      ],
    });
  };

  return (
    <div style={{ width: "100%", textAlign: "center", marginTop: "5rem" }}>
      <h1>Our Employee</h1>
      <button onClick={clickHandler}>Switch</button>
      <User name={userState.users[0].name} age={userState.users[0].age} />
      <User name={userState.users[1].name} age={userState.users[1].age}>
        My Favorite Things: Swiming, Racing
      </User>
      <User name={userState.users[2].name} age={userState.users[2].age} />
      <User name={userState.users[3].name} age={userState.users[3].age} />
    </div>
  );
}

export default App;
