import React, { useState, useRef } from "react";
import Paper from "@mui/material/Paper";
import {
  Scheduler,
  WeekView,
  AppointmentForm,
} from "@devexpress/dx-react-scheduler-material-ui";
import { ViewState, EditingState } from "@devexpress/dx-react-scheduler";
import CustomForm from "./CustomForm";

const schedulerRef = useRef(null);

const App = () => {
  const [appointments, setAppointments] = useState([
    {
      text: "Meeting",
      startDate: new Date(2023, 1, 25, 9, 30),
      endDate: new Date(2023, 1, 25, 11, 30),
    },
    {
      text: "Go to a gym",
      startDate: new Date(2023, 1, 26, 14, 0),
      endDate: new Date(2023, 1, 26, 15, 0),
    },
  ]);

  const handleAppointmentFormCreated = (data) => {
    data.form = (
      <CustomForm
        scheduler={schedulerRef}
        appointment={data.appointmentData}
        onClose={data.hideForm}
      />
    );
  };

  return (
    <Paper>
      <Scheduler
        dataSource={appointments}
        onAppointmentFormCreated={handleAppointmentFormCreated}
      >
        <ViewState />
        <WeekView />
      </Scheduler>
    </Paper>
  );
};

export default App;
